/*!
 * Scripts that run during installation.
 * @since 1.2.6[b]
 */
jQuery(document).ready($=>{'use strict';$('body').on('submit','.data-form',function(e){e.preventDefault();let content=$('.wrapper').html();let site_title=$('#site-title').val();let username=$('#username').val();let password=$('#password').val();let admin_email=$('#admin-email').val();let do_robots=$('#do-robots').prop('checked');$('#submit-ajax').val(1);$('.wrapper').html('<div class="spinner"><i class="fas fa-spinner"></i><br><span>Installing...</span></div>');$.ajax({contentType:!1,data:new FormData(this),method:'POST',processData:!1,success:result=>{result=result.split(';');let error=result[0];let message=result[1];if(error){$('.wrapper').html(content);$('#site-title').val(site_title);$('#username').val(username);$('#password').val(password);$('#admin-email').val(admin_email);$('#do-robots').prop('checked',do_robots);$('#submit-ajax').val(1);if($('.status-message').length>0){$('.status-message').text(message)}else{$('<p class="status-message failure">'+message+'</p>').insertBefore('.data-form')}}else{$('.wrapper').html(message)}},url:'/admin/includes/run-install.php'})})})